let quarters = 25;
let dimes = 10;
let nickels = 5;
let pennies = 1;

document.getElementById("calculate").addEventListener("click", calculate);

function calculate() {
    let numQ = 0;
    let numD = 0;
    let numN = 0;
    let numP = 0;
    let total = 0;
    total = document.getElementById("cents").value;
    parseInt(total, 10);
    while (total / quarters >= 1) {
        numQ++;
        total -= quarters;
    }
    while (total / dimes >= 1) {
        numD++;
        total -= dimes;
  
    }
    while (total / nickels >= 1) {
        numN++;
        total -= nickels;
    }
    while (total / pennies >= 1) {
        numP++;
        total -= pennies;
    }
    document.getElementById("quarters").value = numQ;
    document.getElementById("dimes").value = numD;
    document.getElementById("nickels").value = numN;
    document.getElementById("pennies").value = numP;
}

